registerWorldRenderer(function(context)
    local entities = world.getEntities()

    for index, entity in ipairs(entities) do
        if entity ~= nil and entity.head ~= nil then
            local texture = entity.head.head_texture
            
            if 
            texture == ""
            or texture == ""
            or texture == ""
            or texture == ""
            or texture == ""
            or texture == ""
            or texture == ""
            or texture == ""
            or texture == ""
            --or texture == ""
            or texture == ""
            then
                local text = {
                    x = entity.x, y = entity.y + 2.5, z = entity.z,
                    red = 255, green = 0, blue = 0,
                    scale = 2,
                    text = "Pest", through_walls = true
                }
                context.renderText(text)

                 local line = {
                    x = entity.x, y = entity.y + 2.5, z = entity.z,
                    red = 255, green = 0, blue = 0, alpha = 140,
                    line_width = 2
                }
                context.renderLineFromCursor(line)
            end
        end
    end
end)
